#include <stdint.h>
#include <stdio.h>
#include <string.h>
#include "stm32f4xx.h"
#include "led.h"
#include"systick.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

extern volatile uint32_t xTicks;

int main(void) {
	led_init(LED_BLUE);
	led_init(LED_GREEN);
	led_init(LED_ORANGE);
	led_init(LED_RED);
	// configure SysTick for periodic interrupt of 1ms.
	SysTick_Config(SystemCoreClock / 1000);
	// Toggle LED periodically using SysTick_Delay
	while(1) {
		led_toggle(15);
		SysTick_DelayMs(800);
		led_toggle(12);
		SysTick_DelayMs(800);
		led_toggle(13);
		SysTick_DelayMs(800);
		led_toggle(14);
		SysTick_DelayMs(800);
	}
	return 0;
}





